import React, { useEffect, useState } from "react";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import foire from "../images/Foire BATIMATEC Alger 2016.jpg";

const ActualiteCard = (el) => {
  console.log(el.image);
  /** */
  const myimage = el.image;

  const displaying = (myimage) => {
    // Assuming imageObject is the object you provided
    const { data, contentType } = myimage;

    // Create a data URL from the image data
    const imageDataUrl = `data:${contentType};base64,${Buffer.from(
      data
    ).toString("base64")}`;
    return (
      <div>
        <img src={imageDataUrl} alt="myImage" />
      </div>
    );
  };

  //*/
  const [imageUrl, setImageUrl] = useState([]);

  useEffect(() => {
    // Convert the binary data to a base64 data URL
    const arrayBufferView = new Uint8Array(el.image.data.data);
    const blob = new Blob([arrayBufferView], { type: "Buffer" });
    const urlCreator = window.URL || window.webkitURL;
    const imageUrl = urlCreator.createObjectURL(blob);

    setImageUrl(imageUrl);

    // Clean up the URL when the component unmounts
    return () => urlCreator.revokeObjectURL(imageUrl);
  }, el);
  console.log(imageUrl);
  //** */

  const f = foire;
  return (
    <div>
      <div>
        <Card sx={{ display: "flex" }}>
          <Box sx={{ display: "flex", flexDirection: "column" }}>
            <CardContent sx={{ flex: "1 0 auto" }}>
              <Typography component="div" variant="h5">
                Live From Space55
                <p>{el.title}</p>
              </Typography>
              <Typography
                variant="subtitle1"
                color="text.secondary"
                component="div"
              >
                Mac Miller
              </Typography>
            </CardContent>
          </Box>
          <img src={imageUrl} alt="Uploaded Ima" />
          <CardMedia
            component="img"
            sx={{ width: 151 }}
            alt="Live from space album cover65"
          >
            <displaying />
          </CardMedia>
          <CardMedia />
        </Card>
      </div>
    </div>
  );
};
export default ActualiteCard;
