import React, { useEffect, useState } from "react";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import foire from "../images/Foire BATIMATEC Alger 2016.jpg";

const ActualiteCard = ({ el }) => {
  /** */
  const [imageUrl, setImageUrl] = useState("");

  useEffect(() => {
    // Convert the binary data to a base64 data URL
    const arrayBufferView = new Uint8Array(el.data.data);
    const blob = new Blob([arrayBufferView], { type: el.contentType });
    const urlCreator = window.URL || window.webkitURL;
    const imageUrl = urlCreator.createObjectURL(blob);

    setImageUrl(imageUrl);

    // Clean up the URL when the component unmounts
    return () => urlCreator.revokeObjectURL(imageUrl);
  }, [el]);
  //** */

  console.log(el.image);
  const f = foire;
  return (
    <div>
      <div>
        <Card sx={{ display: "flex" }}>
          <Box sx={{ display: "flex", flexDirection: "column" }}>
            <CardContent sx={{ flex: "1 0 auto" }}>
              <Typography component="div" variant="h5">
                Live From Space55
                <p>{el.title}</p>
              </Typography>
              <Typography
                variant="subtitle1"
                color="text.secondary"
                component="div"
              >
                Mac Miller
              </Typography>
            </CardContent>
          </Box>
          <CardMedia
            component="img"
            sx={{ width: 151 }}
            image={imageUrl}
            alt="Live from space album cover"
          />
          <CardMedia />
        </Card>
      </div>
    </div>
  );
};
export default ActualiteCard;
