import { GET_ARTICLE, CREATE_ARTICLE } from "./types";
import axios from "axios";

//fetch Data
export const getArticles = () => (dispatch) => {
  axios
    .get("http://localhost:5000/api/articales")
    .then((res) => dispatch({ type: GET_ARTICLE, payload: res.data }))
    .catch((err) => console.log(err));
};
//add new Articles
export const addArticles = (newArticle) => (dispatch) => {
  axios
    .post("http://localhost:5000/api/products", newArticle)
    .then((res) => dispatch({ type: CREATE_ARTICLE, payload: res.data }))
    .catch((err) => console.log(err));
};
