import "./App.css";
import { Routes, Route, Link } from "react-router-dom";
import Articleslist from "./components/Articaleslist";
import Accueil from "./components/Accueil";
import ProductList from "./components/ProductList";
import Admin from "./components/Admin";
import AddArticles from "./components/AddArticles";
import AddProduits from "./components/AddProduits";
import Historique from "./components/Historique";
import LoginPage from "./components/LoginPage";
import { Fragment } from "react";

const ProtectedAdminRoute = ({ component: Component, ...rest }) => {
  const isAuthenticated = localStorage.getItem("token"); // Check if token exists in localStorage

  return (
    // <Route
    //   {...rest}
    //   render={(props) =>
    //     isAuthenticated ? <Component {...props} /> : <Link to="/login" />
    //   }
    // />
    <Route
      {...rest}
      render={(props) =>
        isAuthenticated ? <Component {...props} /> : <Link to="/login" />
      }
    />
  );
};

function App() {
  return (
    <div className="App">
      {/* <Accueil /> */}
      <Routes>
        <Fragment>
          <Route path="/" element={<Accueil />} />
          <Route path="/listarticles" element={<Articleslist />} />
          <Route path="/listproduits" element={<ProductList />} />
          <Route path="/histoire" element={<Historique />} />
          <Route path="/admin" element={<ProtectedAdminRoute />}>
            <Route path="/admin" element={<Admin />} />
          </Route>
          {/* <ProtectedAdminRoute path="/admin" element={<Admin />} /> */}
          <Route path="/login" element={<LoginPage />} />
          <Route path="/admin/addarticles" element={<AddArticles />} />
          <Route path="/admin/addproduits" element={<AddProduits />} />
        </Fragment>
      </Routes>
    </div>
  );
}

export default App;
