import { GET_ARTICLE } from "../actions/types";

const initialState = [];

export const articaleReducer = (articles = initialState, action) => {
  switch (action.type) {
    case GET_ARTICLE:
      return {
        ...articles,
        articales: action.payload,
      };
    default:
      return articles;
  }
};
