import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addActualites } from "../redux/actions/actualites";
import AdminNav from "./AdminNav";
const AddActualites = () => {
  const [image, setimage] = useState("");
  const [imageFile, setImageFile] = useState(null); // Store the selected file
  const [title, settitle] = useState("");
  const [description, setdescription] = useState("");

  const dispatch = useDispatch();

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 10 * 1024 * 1024) {
        alert("File size exceeds the allowed limit.");
        e.target.value = null; // Clear the file input
        return;
      }
      // Use the FileReader API to read the selected file
      const reader = new FileReader();
      reader.onloadend = () => {
        const decodedData = atob(reader.result);
        console.log("*****//" + decodedData + "/***********");
        setImageFile(reader.result); // Set the selected file as a base64-encoded string
      };
      reader.readAsDataURL(file);
    }
  };
  //const added_actualite = { title,  image: imageFile, description };
  const add = (e) => {
    e.preventDefault();
    const added_actualite = { title, image: imageFile, description };
    console.log(added_actualite);
    dispatch(addActualites(added_actualite));
  };
  return (
    <div>
      <AdminNav />
      <h1>Gestion des actualites</h1>
      <form action="/" method="post" enctype="multipart/form-data">
        <label htmlFor=""> image</label>
        {/* <input
          type="file"
          name="image"
          onChange={(e) => setimage(e.target.value)}
        /> */}
        <input
          type="file"
          id="image"
          accept="image/*"
          onChange={handleImageChange}
        />
        <br />

        <label htmlFor=""> Title</label>
        <input
          type="text"
          value={title}
          onChange={(e) => settitle(e.target.value)}
        />
        <br />

        <label htmlFor=""> description</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setdescription(e.target.value)}
        />
        <br />
        <button type="submit" onClick={add}>
          Ajouter
        </button>
      </form>
    </div>
  );
};

export default AddActualites;
