import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addActualites } from "../redux/actions/actualites";
import AdminNav from "./AdminNav";
const AddActualites = () => {
  //const [image, setimage] = useState("");
  const [imageData, setImageData] = useState({
    data: {
      type: "Buffer",
      data: [],
    },
    contentType: "image/jpeg", // Replace with the actual image type
  });
  //const [imageFile, setImageFile] = useState(null); // Store the selected file
  const [title, settitle] = useState("");
  const [description, setdescription] = useState("");

  const dispatch = useDispatch();

  // const handleImageChange = (e) => {
  //   const file = e.target.files[0];
  //   if (file) {
  //     if (file.size > 10 * 1024 * 1024) {
  //       alert("File size exceeds the allowed limit.");
  //       e.target.value = null; // Clear the file input
  //       return;
  //     }
  //     // Use the FileReader API to read the selected file
  //     const reader = new FileReader();
  //     reader.onload = (event) => {
  //       const arrayBuffer = event.target.result;

  //       // Convert the ArrayBuffer to Blob
  //       const blob = new Blob([arrayBuffer], {
  //         contentType: file.type,
  //       });

  //       // Set the Blob as the image file
  //       setImageFile(blob);
  //     };
  //     reader.readAsArrayBuffer(file);
  //   }
  // };
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Use the FileReader API to read the selected file as ArrayBuffer
      const reader = new FileReader();
      reader.onload = (event) => {
        const arrayBuffer = event.target.result;

        // Convert the ArrayBuffer to an array of integers (Uint8Array)
        const uint8Array = new Uint8Array(arrayBuffer);

        // Update the imageData state
        setImageData({
          data: {
            type: "Buffer",
            data: Array.from(uint8Array), // Convert Uint8Array to regular array
          },
          contentType: file.type,
          originalName: file.name, // Store the original filename
        });
      };
      reader.readAsArrayBuffer(file);
    }
  };
  //const added_actualite = { title,  image: imageFile, description };

  // const add = (e) => {
  //   e.preventDefault();
  //   const added_actualite = { title, image: imageData, description };
  //   console.log(imageData);
  //   dispatch(addActualites(added_actualite));
  // };
  const clearForm = () => {
    settitle("");
    setdescription("");
    setImageData({
      data: {
        type: "Buffer",
        data: [],
      },
      contentType: "image/jpeg", // Reset to initial state
    });
  };

  const add = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    // formData.append(
    //   "image",
    //   new Blob([new Uint8Array(imageData.data.data)], {
    //     type: imageData.contentType,
    //   })
    // );

    // Use the original filename if available, otherwise use a default name
    formData.append("image", imageData, imageData.name);
    console.log(formData.name);
    console.log(formData);

    dispatch(addActualites(formData));
    // Clear the form fields and FormData
    clearForm();
  };
  return (
    <div>
      <AdminNav />
      <h1>Gestion des actualites</h1>
      {/* <form action="/" method="post" enctype="multipart/form-data"> */}
      <div>
        <label htmlFor=""> image</label>
        {/* <input
          type="file"
          name="image"
          onChange={(e) => setimage(e.target.value)}
        /> */}
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <br />

        <label htmlFor=""> Title</label>
        <input
          type="text"
          value={title}
          onChange={(e) => settitle(e.target.value)}
        />
        <br />

        <label htmlFor=""> description</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setdescription(e.target.value)}
        />
        <br />
        <button type="button" onClick={add}>
          Ajouter
        </button>
      </div>
      {/* </form> */}
    </div>
  );
};

export default AddActualites;
