import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { addActualites } from "../redux/actions/actualites";
import AdminNav from "./AdminNav";
<link
  href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css"
  rel="stylesheet"
  integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN"
  crossorigin="anonymous"
/>;

const AddActualites = () => {
  //const [image, setimage] = useState("");
  const [imageData, setImageData] = useState({
    data: {
      type: "Buffer",
      data: [],
    },
    contentType: "image/jpeg", // Replace with the actual image type
  });
  const [title, settitle] = useState("");
  const [description, setdescription] = useState("");

  const dispatch = useDispatch();

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Use the FileReader API to read the selected file as ArrayBuffer
      const reader = new FileReader();
      reader.onload = (event) => {
        const arrayBuffer = event.target.result;

        // Convert the ArrayBuffer to an array of integers (Uint8Array)
        const uint8Array = new Uint8Array(arrayBuffer);

        // Update the imageData state
        setImageData({
          data: {
            type: "Buffer",
            data: Array.from(uint8Array), // Convert Uint8Array to regular array
          },
          contentType: file.type,
          originalName: file.name, // Store the original filename
        });
      };
      reader.readAsArrayBuffer(file);
    }
  };

  const clearForm = () => {
    settitle("");
    setdescription("");
    setImageData(null);
  };

  const add = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);

    formData.append(
      "image",
      new Blob([new Uint8Array(imageData.data.data)], {
        type: imageData.contentType,
      }),
      imageData.originalName
    );
    dispatch(addActualites(formData));
    console.log(formData);
    // Clear the form fields and FormData
    clearForm();
    alert("actualité ajouter");
  };
  return (
    <div>
      <AdminNav />
      <h1>Ajouter des actualites</h1>
      <form action="/" method="post" enctype="multipart/form-data">
        <label htmlFor=""> image</label>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <br />

        <label htmlFor=""> Title</label>
        <input
          type="text"
          value={title}
          onChange={(e) => settitle(e.target.value)}
        />
        <br />

        <label htmlFor=""> description</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setdescription(e.target.value)}
        />
        <br />
        <button type="button" onClick={add}>
          Ajouter
        </button>
      </form>
      <div>
        <form>
          <div class="form-group">
            <label for="validationCustom01">Actualité title</label>
            <input
              type="text"
              class="form-control"
              id="validationCustom01"
              placeholder="Actualité title"
              onChange={(e) => settitle(e.target.value)}
              value={title}
              required
            />
          </div>
          <div class="form-group">
            <label for="validationCustom01">Description</label>
            <input
              type="text"
              class="form-control"
              id="validationCustom01"
              placeholder="Actualité description"
              value={description}
              onChange={(e) => setdescription(e.target.value)}
              required
            />
          </div>
          <br />
          <div class="mb-3">
            <input
              class="form-control"
              type="file"
              id="formFile"
              accept="image/*"
              onChange={handleImageChange}
            />
          </div>

          <button type="button" onClick={add} class="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddActualites;
