import { GET_ARTICLE } from "../actions/types";

const initialState = {
  articles: [],
};

export const articaleReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_ARTICLE:
      return {
        ...state,
        articales: action.payload,
      };
    default:
      return state;
  }
};
export default articaleReducer;
