import { GET_ARTICLE, CREATE_ARTICLE } from "./types";
import axios from "axios";

//fetch Data
export const getActualites = () => (dispatch) => {
  axios
    .get("http://localhost:5000/api/actualites")
    .then((res) => dispatch({ type: GET_ARTICLE, payload: res.data }))
    .catch((err) => console.log(err));
};
//add new Actualites
export const addActualites = (newActualite) => (dispatch) => {
  axios
    .post("http://localhost:5000/api/actualites", newActualite)
    .then((res) => dispatch({ type: CREATE_ARTICLE, payload: res.data }))
    .catch((err) => console.log(err));
};
