import { GET_ARTICLE } from "../actions/types";

const initialState = {
  articales: [],
};

export const articaleReducer = (articales = initialState, action) => {
  switch (action.type) {
    case GET_ARTICLE:
      return {
        ...articales,
        articales: action.payload,
      };
    default:
      return articales;
  }
};
